import dotenv from "dotenv";
import path from "path";
dotenv.config();
const environment = process.env.NODE_ENV;

let baseMongoUrl = process.env.MONGO_URL;

const cloudFolders = {
  articles: "articles",
  profiles: "profiles",
};

let envTag;

switch (environment && environment.toLowerCase()) {
  //make sure db are different for each environment
  case "development":
    envTag = "-dev";
    break;
  case "test":
    envTag = "-test";
    break;
  case "production":
    envTag = "";
    break;
  default:
  // baseMongoUrl;
}

// Make sure that resources are different for each environment
baseMongoUrl += envTag;
for (let key in cloudFolders) {
  cloudFolders[key] += envTag;
}
export const cloudinaryFolders = cloudFolders;
export const uploadFolder = path.join(
  __dirname,
  "..",
  "public",
  "uploads",
  "temps"
);

export const port = process.env.PORT || 5000;
export const cloudinaryApiKey = process.env.CLOUDINARY_API_KEY;
export const cloudinaryApiSecret = process.env.CLOUDINARY_API_SECRET;
export const cloudName = process.env.CLOUD_NAME;
export const mongoUrl = baseMongoUrl;
export const tokenSecret = process.env.TOKEN_SECRET;

import routes from "./routes";
import express from "express";
import { SampleController } from "./controllers";
import fs from "fs";
import { uploadFolder } from "./config";
if (!fs.existsSync(uploadFolder))
  fs.mkdirSync(uploadFolder, { recursive: true });
export default function setupApp(app) {
  app.use(express.json());
  app.use(express.urlencoded({ extended: true }));
  app.use("/public", express.static(uploadFolder));
  app.use("/api", routes);
  app.use("*", SampleController.notFound);
}

import express from "express";
import { SampleController } from "../controllers/";
import messageRoutes from "./message";
import articlesRoutes from "./articles";
import userRoutes from "./user";
import authRoutes from "./auth";
import { isLoggedIn } from "../middlewares/auth";
import { is } from "express/lib/request";
const router = express.Router();
router.get("/", (request, response) => {
  response.status(200).json({ message: "Welcome" });
});
router.use("/messages", messageRoutes);
router.use("/articles", isLoggedIn, articlesRoutes);
router.use("/user", isLoggedIn, userRoutes);
router.use("/auth", authRoutes);
router.use("*", SampleController.notFound);
