      if (!Comment) return { succes: true, message: "Comment not found" };
      return { success: true, comment };
    } catch (error) {
      return { success: false, error: error.message };
    }
  }
  static async getComments({ count = 100, skip = 0, filter = {} }) {
    try {
      const comments = await Comment.find(filter)
        .populate("reply")
        .limit(100)
        .skip(skip * count);
      if (!comments) return { succes: true, error: "Comments not found" };
      return { success: true, comments };
    } catch (error) {
      return { success: false, error: error.message };
    }
  }
  static async addArticleImages(id, { image }) {
    try {
      const article = await Article.findOne({ _id: id });
      // article.images.push()
      return { success: true, article };
    } catch (error) {
      return { success: false, error: error.message };
    }
  }

  // categories

  static async getCategories({ skip = 0, count = 100 }) {
    try {
      const categories = await Category.find({})
        .limit(count)
        .skip(count * skip);
      if (!categories) return { success: false, error: "Categories not found" };
      return { success: true, categories };
    } catch (error) {
      return { success: false, error: error.message };
    }
  }
  static async getCategory(id, { articles, skip = 0, count = 100 }) {
    try {
      let category;
      if (!articles) category = await Category.findOne({ _id: id });
      else
        category = await Category.findOne({ _id: id }).populate({
          path: "articles",
          options: { limit: count, skip: count * skip },
        });
      if (!category) return { success: false, error: "Category not found" };
      return { success: true, category };
    } catch (error) {
      return { success: false, error: error.message };
    }
  }
  static async updateCategory(id, { title, description }) {
    try {
      const category = await Category.findOne({ _id: id });

      if (!category) return { success: false, error: "Category not found" };
      if (title)
        category.title = title[0].toUpperCase() + title.slice(1).toLowerCase();
      if (description) category.description = description;
      await category.save();
      return { success: true, category };
    } catch (error) {
      return { success: false, error: error.message };
    }
  }
}

import path, { resolve } from "path";
import multer from "multer";
import {
  uploadFolder,
  cloudName,
  cloudinaryApiKey,
  cloudinaryApiSecret,
} from "../config";

import { v2 as cloudinary } from "cloudinary";

cloudinary.config({
  cloud_name: cloudName,
  api_key: cloudinaryApiKey,
  api_secret: cloudinaryApiSecret,
  secure: true,
});

const storage = multer.diskStorage({
  destination: function (request, file, cb) {
    cb(null, uploadFolder);
  },
  filename: function (request, file, cb) {
    const uniqueSuffix = Date.now() + "-" + Math.round(Math.random() * 1e9);
    cb(
      null,
      file.fieldname + "-" + uniqueSuffix + path.extname(file.originalname)
    );
  },
});

export const cloudinaryUploader = (file, folder) => {
  return new Promise((resolve) => {
    cloudinary.uploader.upload(file, { folder }, function (error, result) {
      if (error) console.warn(error);
      resolve(result);
    });
  });
};
export const multerUploader = multer({ storage });

export { default as Article } from "./Article";
export { default as Category } from "./Category";
export { default as Comment } from "./Comment";
export { default as User } from "./User";
export { default as Message } from "./Message";

import mongoose from "mongoose";
const articleSchema = new mongoose.Schema(
  {
    title: { type: String, required: true, unique: false },
    images: [{ path: String, height: Number, width: Number, info: String }],
    summary: { type: String, required: true },
    content: { type: String, required: true },
    categories: [{ type: mongoose.Schema.Types.ObjectId, ref: "Category" }],
    published: { type: Boolean, required: true, default: false },
    featured: { type: Boolean, required: true, default: false },
    author: { type: mongoose.Types.ObjectId, ref: "User" },
    comments: [{ type: mongoose.Types.ObjectId, ref: "Comment" }],
  },
  { timestamps: true }
);
export default mongoose.model("Article", articleSchema);

import { Schema, model } from "mongoose";
const categorySchema = new Schema(
  {
    title: { type: String, required: true },
    description: { type: String, required: false, default: "" },
    articles: [{ type: Schema.Types.ObjectId, ref: "Article" }],
  },
  { timestamps: true }
);
categorySchema.pre("validate", function () {
  this.title = this.title[0].toLowerCase() + this.title.slice(1).toLowerCase();
});
export default model("Category", categorySchema);

import mongoose from "mongoose";
const commentSchema = new mongoose.Schema(
  {
    author: { type: mongoose.Schema.Types.ObjectId, ref: "User" },
    comment: { type: String, required: true },
    votes: { type: Number, required: true, default: 0 },
    read: { type: Boolean, required: true, default: false },
    reply: [{ type: mongoose.Schema.Types.ObjectId, ref: "Comment" }],
  },
  { timestamps: true }
);
export default mongoose.model("Comment", commentSchema);

import mongoose from "mongoose";
import { hash, compare } from "bcrypt";
const userSchema = new mongoose.Schema(
  {
    name: { type: String, required: true },
    email: { type: String, required: true, unique: true },
    username: { type: String, required: true, unique: true },
    password: { type: String, required: true },
    verified: { type: Boolean, required: true, default: false },
    keywords: { type: String },
    summary: { type: String },
    info: { type: String },
    profilePic: {
      path: String,
      width: String,
      height: String,
    },
    star: { type: Number, default: 0 },
    tokens: [{ type: String }],
  },
  { timestamps: true }
);
userSchema.pre("save", async function (next) {
  if (!this.isModified("password")) return next();
  this.password = await hash(this.password, 10);
  next();
});

userSchema.methods.comparePassword = async function (password) {
  return await compare(password, this.password);
};
export default mongoose.model("User", userSchema);

import { cloudinaryFolders } from "../config";
import { cloudinaryUploader } from "../helpers/uploader";
import { User } from "../models";
export default class UserServive {
  static async signup({ name, username, email, password, profilePic }) {
    try {
      let user;
      if (!profilePic)
        user = new User({
          name,
          username,
          email,
          password,
        });
      else {
        let uploadResult = await cloudinaryUploader(
          profilePic,
          cloudinaryFolders.profiles
        );
        user = new User({
          name,
          username,
          email,
          password,
          profilePic: {
            path: uploadResult.secure_url || uploadResult.url,
            width: uploadResult.width,
            height: uploadResult.height,
          },
        });
      }
      await user.save();
      return { success: true, user };
    } catch (error) {
      return { error: error.message };
    }
  }
  static async updateUser(
    id,
    {
      name = null,
      email = null,
      username = null,
      password = null,
      verified = null,
      keywords = null,
      summary = null,
      info = null,
      profilePic,
      token = null,
      star = null,
    }
  ) {
    try {
      const user = await User.findOne({ _id: id });
      if (!user)
        return {
          success: false,
          error: "User not found, you might need to login",
        };
      if (name) user.name = name;
      if (email) user.email = email;
      if (username) user.username = username;
      if (password) user.password = password;
      if (verified) user.verified = verified;
      if (keywords) user.keywords = keywords;
      if (summary) user.summary = summary;
      if (info) user.info = info;
      if (profilePic) {
        let uploadResult = await cloudinaryUploader(profilePic);
        user.profilePic = {
          path: uploadResult.path,
          width: uploadResult.width,
          height: uploadResult.height,
        };
      }
      if (token) {
        token.type > 0
          ? user.tokens.push(token.value)
          : user.tokens.pull(token.value);
      }
      if (star) user.star = star;
      await user.save();
      return { success: true, user };
    } catch (error) {
      return { error: error.message };
    }
  }
  static async deleteUser(id) {
    try {
      const user = await User.findOneAndDelete({ _id: id });
      if (!user)
        return {
          success: false,
          error: "User not found or you might need to login",
        };
      return { success: true, user };
    } catch (error) {
      return { error: error.message };
    }
  }
  static async getUser(id) {
    try {
      const userData = await User.findOne({ _id: id });
      if (!userData)
        return {
          success: false,
          error: "User not found or you might need to login",
        };
      let user = Object.assign({}, userData._doc, { password: undefined });

      return { success: true, user };
    } catch (error) {
      return { error: error.message };
    }
  }

  static async getUsers({ skip = 0, count = 100, filter = {} }) {
    try {
      const users = await User.find(filter)
        .limit(count)
        .skip(skip * count);
      if (!users) return { success: false, error: "Users not found" };
      return { success: true, users };
    } catch (error) {
      return { success: false, error: error.message };
    }
  }
}

import { ArticleServive } from "../services";
import { unlinkSync } from "fs";
export default class Article {
  static async addArticle(request, response) {
    try {
      if (!request.file)
        return response
          .status(400)
          .json({ status: 400, error: "Image required" });
      const { path: image } = request.file;

      const { title, images, summary, content, categories } = request.body;
      const result = await ArticleServive.addArticle({
        title,
        images,
        summary,
        content,
        categories,
